import React, { useEffect, useState } from "react";
import "./css/style.css";
const Tempapp = () => { 
    const [city, setCity] = useState(null);
    const [search, setSearch] = useState(null);

    useEffect( () => {
        const fetchApi = async ()=>{
            const url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=b4c5c299ddedd7a78faae8a062b68991
`;            
            const response = await fetch(url);
            // console.log(response);
            const resJson = await response.json();
            // console.log(resJson);
            setCity(resJson.main);

        }
        fetchApi();
    }, [search])
    return(
        <>
            <div className="box">
                <div className="inputData">
                    <input type="search" className="inputField"
                        onChange={(event) => {
                            setSearch(event.target.value)
                        }} />
                </div>
                {
                    !city ? (
                        <p className="errorMsg">NO DATA FOUND</p>
                    ) : (
                    <div>    
                    <div className="info">
                    <h2 className="location">
                    <i className="fa-solid fa-street-view"></i>
                    <p>{search}</p>
                    </h2>
                    <h1 className="temp">
                        {city.temp} °C
                    </h1>
                    <h3 className="tempmin_max">Max: {city.temp_max}°C    Min: {city.temp_min}°C</h3>
                    </div>
                    </div>
                    )
                }              
            </div> 
        </>
    )
}

export default Tempapp;
